#include "leaks.h"
#include "parser.h"
typedef struct child_node{ char *id; double leak_percent; struct child_node *next;} child_node_t;
typedef struct node{ char *id; child_node_t *children;} node_t;
static void node_free(void *v){ node_t *n=v; if(!n) return; child_node_t *c=n->children; while(c){ child_node_t *nx=c->next; free(c->id); free(c); c=nx;} free(n->id); free(n);}
double compute_leaks_for_factory(FILE *f,const char *factory_id){ rewind(f); struct avl_tree *nodes=avl_create(); char line[MAX_LINE_LEN]; while(fgets(line,sizeof(line),f)){ csv_line_t *r=csv_line_parse(line); if(!r) continue; if(!r->col1 || strcmp(r->col1,factory_id)!=0){ csv_line_free(r); continue;} node_t *p=avl_get(nodes,r->col2); if(!p){ p=calloc(1,sizeof(*p)); p->id=xstrdup(r->col2); avl_insert(nodes,p->id,p);} node_t *ch=avl_get(nodes,r->col3); if(!ch){ ch=calloc(1,sizeof(*ch)); ch->id=xstrdup(r->col3); avl_insert(nodes,ch->id,ch);} child_node_t *c=calloc(1,sizeof(*c)); c->id=xstrdup(r->col3); c->leak_percent=(r->col5&&strcmp(r->col5,"-")!=0)?strtod(r->col5,NULL):0.0; c->next=p->children; p->children=c; csv_line_free(r);} node_t *start=avl_get(nodes,factory_id); if(!start){ avl_free(nodes,node_free); return -1.0;}
typedef struct qitem{ node_t *n; double flow; struct qitem *next;} qitem_t; qitem_t *qh=NULL,*qt=NULL; qitem_t *fi=calloc(1,sizeof(*fi)); fi->n=start; fi->flow=1.0; qh=qt=fi; double leak=0.0; while(qh){ qitem_t *it=qh; qh=qh->next; if(!qh) qt=NULL; node_t *n=it->n; double flow=it->flow; int cnt=0; for(child_node_t *c=n->children;c;c=c->next) cnt++; if(cnt>0){ double per=flow/cnt; for(child_node_t *c=n->children;c;c=c->next){ double lost=per*(c->leak_percent/100.0); leak+=lost; double fwd=per-lost; node_t *child=avl_get(nodes,c->id); if(child){ qitem_t *ni=calloc(1,sizeof(*ni)); ni->n=child; ni->flow=fwd; if(qt) qt->next=ni; else qh=ni; qt=ni;} } } free(it);} avl_free(nodes,node_free); return leak;}